enum Error {
    TooBig {
            given: u64,
            max: u64,
        },
    BufferTooSmall {
            given: u64,
            min: u64,
        },
    Empty,
    Header,
    HeaderMismatch {
            expected_len: u64,
            got_len: u64,
        },
    Literal {
            len: u64,
            src_len: u64,
            dst_len: u64,
        },
    CopyRead {
            len: u64,
            src_len: u64,
        },
    CopyWrite {
            len: u64,
            dst_len: u64,
        },
    Offset {
            offset: u64,
            dst_pos: u64,
        },
    StreamHeader {
            byte: u8,
        },
    StreamHeaderMismatch {
            bytes: Vec<u8>,
        },
    UnsupportedChunkType {
            byte: u8,
        },
    UnsupportedChunkLength {
            len: u64,
            header: bool,
        },
    Checksum {
            expected: u32,
            got: u32,
        },
}
